import React, { useEffect, useState } from "react";
import { Modal, Spin, Typography, Image } from "antd";
import { useGetCiltMstrByIdMutation } from "../../../services/cilt/ciltMstrService";
import Strings from "../../../utils/localizations/Strings";

const { Text } = Typography;

interface DiagramViewerModalProps {
  open: boolean;
  ciltId: string | number | null;
  onClose: () => void;
  title: string;
}

/**
 * Modal component to view CILT diagram
 */
const DiagramViewerModal: React.FC<DiagramViewerModalProps> = ({
  open,
  ciltId,
  onClose,
  title
}) => {
  // Get CILT master data mutation
  const [getCiltMstrById, { isLoading, error }] = useGetCiltMstrByIdMutation();
  const [imageUrl, setImageUrl] = useState<string | null>(null);

  useEffect(() => {
    const fetchDiagram = async () => {
      if (open && ciltId) {
        try {
          // Fetch CILT master data
          const ciltData = await getCiltMstrById(String(ciltId)).unwrap();
          
          if (ciltData?.urlImgLayout) {
            setImageUrl(ciltData.urlImgLayout);
          } else {
            setImageUrl(null);
          }
        } catch (err) {
          console.error("Error fetching diagram:", err);
          setImageUrl(null);
        }
      }
    };

    fetchDiagram();
  }, [open, ciltId, getCiltMstrById]);

  // Handle modal close
  const handleClose = () => {
    onClose();
    // Reset state
    setImageUrl(null);
  };

  return (
    <Modal
      title={title}
      open={open}
      onCancel={handleClose}
      width={900}
      footer={null}
      centered
      bodyStyle={{ maxHeight: 'calc(100vh - 200px)', overflow: 'auto', paddingRight: 20 }}
      style={{ top: 20 }}
    >
      {isLoading ? (
        <div className="flex justify-center items-center py-12">
          <Spin size="large" />
        </div>
      ) : error ? (
        <div className="p-8 text-center">
          <Text type="danger">{Strings.errorOnLoadingData}</Text>
        </div>
      ) : imageUrl ? (
        <div className="flex justify-center items-center overflow-visible">
          <Image 
            src={imageUrl} 
            alt={title} 
            className="max-w-full"
            fallback="data:image/png;base64,"
          />
        </div>
      ) : (
        <div className="p-8 text-center">
          <Text>{Strings.noDataAvailable}</Text>
        </div>
      )}
    </Modal>
  );
};

export default DiagramViewerModal;
